LUNGO.Sugar.Talk=function(a){var d=function(){a.View.Template.create("sugar-talk-tmp",'<li class="talk {{me}}">            <img src="{{thumb}}" />            <span class="who">{{who}}<span class="timestamp">{{timestamp}}</span></span>            <span class="message">{{message}}</small>        </li>')};d();return{init:d,create:function(b,c){a.View.Template.List.create({el:b,template:"sugar-talk-tmp",data:c})},append:function(b,c){a.View.Template.List.append({el:b,template:"sugar-talk-tmp",data:c});
a.View.Scroll.last(b)}}}(LUNGO);
